mapper.select:
selectfrom TB4D_DATABASE_LINK
         and DBLINK_VALUE like #{dblinkValue} 
         and DBLINK_NAME like #{dblinkName} 
         and DBLINK_TYPE like #{dblinkType} 
         and DBLINK_DRIVERNAME like #{dblinkDrivername} 
         and DBLINK_URL like #{dblinkUrl} 
         and DBLINK_USER like #{dblinkUser} 
         and DBLINK_PASSWORD like #{dblinkPassword} 
         and DBLINK_DESC like #{dblinkDesc} 
         and DBLINK_IS_LOCATION like #{dblinkIsLocation} 
         and DBLINK_STATUS like #{dblinkStatus} 
    ORDER by DBLINK_ORDER_NUM DESC
